    });

    const birdCategory = await storage.createCategory({
      name: "Birds",
      description: "Feathered friends and bird care products",
    });

    console.log("âœ… Categories created");

    // Create sample products
    console.log("Creating sample products...");

    // Dog products
    await storage.createProduct({
      name: "Golden Retriever Puppy",
      categoryId: dogCategory.id,
      type: "pet",
      species: "Dog",
      description: "Friendly and energetic golden retriever puppy, perfect for families with children. Well-socialized and health-checked.",
