  });

  const handleCreate = () => {
    setEditingCategory(null);
    setCategoryData({ name: "", description: "" });
    setIsCreateOpen(true);
  };

  const handleEdit = (category: Category) => {
    setEditingCategory(category);
    setCategoryData({
      name: category.name,
      description: category.description,
    });
    setIsCreateOpen(true);
  };

  const handleCloseDialog = () => {
    setIsCreateOpen(false);
    setEditingCategory(null);
    setCategoryData({ name: "", description: "" });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingCategory) {
      updateCategoryMutation.mutate({ id: editingCategory.id, data: categoryData });
    } else {
      createCategoryMutation.mutate(categoryData);
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this category? This action cannot be undone.")) {
      deleteCategoryMutation.mutate(id);
    }
  };

  const isLoading = createCategoryMutation.isPending || updateCategoryMutation.isPending;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-serif font-bold mb-2">Category Management</h2>
          <p className="text-muted-foreground">Organize your products with categories</p>
        </div>
        <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleCreate} data-testid="button-add-category">
              <Plus className="mr-2 h-4 w-4" />
              Add Category
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md forest-card">
            <DialogHeader>
              <DialogTitle>
                {editingCategory ? "Edit Category" : "Create New Category"}
              </DialogTitle>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="category-name">Name *</Label>
                <Input
                  id="category-name"
                  type="text"
                  placeholder="Category name"
                  value={categoryData.name}
                  onChange={(e) => setCategoryData(prev => ({ ...prev, name: e.target.value }))}
                  required
                  disabled={isLoading}
                  data-testid="input-category-name"
                />
              </div>

              <div>
                <Label htmlFor="category-description">Description</Label>
                <Textarea
                  id="category-description"
                  placeholder="Category description"
                  value={categoryData.description}
                  onChange={(e) => setCategoryData(prev => ({ ...prev, description: e.target.value }))}
                  disabled={isLoading}
                  className="h-20"
                  data-testid="input-category-description"
                />
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button type="button" variant="outline" onClick={handleCloseDialog} disabled={isLoading}>
                  Cancel
                </Button>
                <Button type="submit" disabled={isLoading} data-testid="button-save-category">
                  {isLoading ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      {editingCategory ? "Updating..." : "Creating..."}
                    </>
                  ) : (
                    editingCategory ? "Update Category" : "Create Category"
                  )}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Categories List */}
      <div className="space-y-4">
        {categories?.map((category) => (
          <Card key={category.id} className="forest-card">
            <CardContent className="p-6">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <h3 className="font-semibold text-lg mb-2" data-testid={`text-category-name-${category.id}`}>
                    {category.name}
                  </h3>
                  <p className="text-muted-foreground text-sm" data-testid={`text-category-description-${category.id}`}>
                    {category.description || "No description provided"}
                  </p>
                  <p className="text-xs text-muted-foreground mt-2">
                    Slug: <span className="font-mono" data-testid={`text-category-slug-${category.id}`}>{category.slug}</span>
                  </p>
                </div>
                <div className="flex space-x-2 ml-4">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleEdit(category)}
                    data-testid={`button-edit-category-${category.id}`}
                  >
                    <Edit className="h-4 w-4 mr-1" />
                    Edit
                  </Button>
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => handleDelete(category.id)}
                    disabled={deleteCategoryMutation.isPending}
                    data-testid={`button-delete-category-${category.id}`}
                  >
                    <Trash2 className="h-4 w-4 mr-1" />
                    Delete
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

        {categories?.length === 0 && (
          <Card className="forest-card">
            <CardContent className="p-12 text-center" data-testid="text-no-categories">
              <p className="text-muted-foreground text-lg">No categories found. Create your first category to get started.</p>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/components/admin/orders-manag.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Eye, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Order } from "@shared/schema";

export default function OrdersManagement() {
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [isDetailOpen, setIsDetailOpen] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: orders, isLoading } = useQuery<Order[]>({
    queryKey: ["/api/admin/orders"],
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const res = await apiRequest("PUT", `/api/admin/orders/${id}/status`, { status });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/orders"] });
      toast({ title: "Order status updated successfully" });
    },
    onError: (error: Error) => {
      toast({ title: "Failed to update order status", description: error.message, variant: "destructive" });
    },
  });

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending":
        return "bg-yellow-500/20 text-yellow-400";
      case "completed":
        return "bg-green-500/20 text-green-400";
      case "cancelled":
        return "bg-red-500/20 text-red-400";
      default:
        return "bg-gray-500/20 text-gray-400";
    }
  };

  const handleViewOrder = (order: Order) => {
    setSelectedOrder(order);
    setIsDetailOpen(true);
  };

  const handleStatusUpdate = (orderId: string, newStatus: string) => {
    updateStatusMutation.mutate({ id: orderId, status: newStatus });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Loader2 className="h-8 w-8 animate-spin text-border" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-serif font-bold mb-2">Order Management</h2>
        <p className="text-muted-foreground">Monitor and manage customer orders</p>
      </div>

      <Card className="forest-card">
        <CardHeader>
          <CardTitle>All Orders</CardTitle>
        </CardHeader>
        <CardContent>
          {orders?.length === 0 ? (
            <div className="text-center py-8" data-testid="text-no-orders">
              <p className="text-muted-foreground">No orders found.</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-border">
                    <th className="text-left py-3 px-4">Order ID</th>
                    <th className="text-left py-3 px-4">Customer</th>
                    <th className="text-left py-3 px-4">Total</th>
                    <th className="text-left py-3 px-4">Status</th>
                    <th className="text-left py-3 px-4">Date</th>
                    <th className="text-left py-3 px-4">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {orders?.map((order) => (
                    <tr key={order.id} className="border-b border-border" data-testid={`row-order-${order.id}`}>
                      <td className="py-3 px-4" data-testid={`text-order-id-${order.id}`}>
                        #{order.id.slice(-8)}
                      </td>
                      <td className="py-3 px-4" data-testid={`text-customer-name-${order.id}`}>
                        {order.customer.name}
                      </td>
                      <td className="py-3 px-4" data-testid={`text-order-total-${order.id}`}>
                        {formatPrice(order.totalAmountINR)}
                      </td>
                      <td className="py-3 px-4">
                        <Badge className={getStatusColor(order.status)} data-testid={`badge-order-status-${order.id}`}>
                          {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                        </Badge>
                      </td>
                      <td className="py-3 px-4" data-testid={`text-order-date-${order.id}`}>
                        {formatDate(order.createdAt)}
                      </td>
                      <td className="py-3 px-4">
                        <div className="flex space-x-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleViewOrder(order)}
                            data-testid={`button-view-order-${order.id}`}
                          >
                            <Eye className="h-4 w-4 mr-1" />
                            View
                          </Button>
                          <Select
                            value={order.status}
                            onValueChange={(value) => handleStatusUpdate(order.id, value)}
                            disabled={updateStatusMutation.isPending}
                          >
                            <SelectTrigger className="w-32" data-testid={`select-status-${order.id}`}>
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="pending">Pending</SelectItem>
                              <SelectItem value="completed">Completed</SelectItem>
                              <SelectItem value="cancelled">Cancelled</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Order Detail Modal */}
      <Dialog open={isDetailOpen} onOpenChange={setIsDetailOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto forest-card" data-testid="modal-order-detail">
          {selectedOrder && (
            <>
              <DialogHeader>
                <DialogTitle>Order Details - #{selectedOrder.id.slice(-8)}</DialogTitle>
              </DialogHeader>

              <div className="space-y-6">
                {/* Customer Information */}
                <div>
                  <h3 className="text-lg font-semibold mb-3">Customer Information</h3>
                  <div className="grid md:grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="font-medium">Name:</span>
                      <span className="ml-2" data-testid="text-detail-customer-name">{selectedOrder.customer.name}</span>
                    </div>
                    <div>
                      <span className="font-medium">Phone:</span>
                      <span className="ml-2" data-testid="text-detail-customer-phone">{selectedOrder.customer.phone}</span>
                    </div>
                    {selectedOrder.customer.altPhone && (
                      <div>
                        <span className="font-medium">Alt Phone:</span>
                        <span className="ml-2" data-testid="text-detail-customer-alt-phone">{selectedOrder.customer.altPhone}</span>
                      </div>
                    )}
                    <div className="md:col-span-2">
                      <span className="font-medium">Address:</span>
                      <span className="ml-2" data-testid="text-detail-customer-address">{selectedOrder.customer.address}</span>
                    </div>
                  </div>
                </div>

                {/* Products */}
                <div>
                  <h3 className="text-lg font-semibold mb-3">Products</h3>
                  <div className="space-y-3">
                    {selectedOrder.products.map((product, index) => (
                      <div key={index} className="forest-card p-4 rounded-lg" data-testid={`item-order-product-${index}`}>
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-medium" data-testid={`text-product-name-${index}`}>{product.name}</h4>
                            <p className="text-sm text-muted-foreground">
                              Quantity: <span data-testid={`text-product-quantity-${index}`}>{product.quantity}</span>
                            </p>
                          </div>
                          <div className="text-right">
                            <p className="font-medium" data-testid={`text-product-total-${index}`}>
                              {formatPrice(product.priceInINR * product.quantity)}
                            </p>
                            <p className="text-sm text-muted-foreground">
                              {formatPrice(product.priceInINR)} each
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Order Summary */}
                <div className="border-t border-border pt-4">
                  <div className="flex justify-between items-center text-lg font-semibold">
                    <span>Total Amount:</span>
                    <span className="text-accent" data-testid="text-detail-total">
                      {formatPrice(selectedOrder.totalAmountINR)}
                    </span>
                  </div>
                  <div className="flex justify-between items-center text-sm text-muted-foreground mt-2">
                    <span>Order Date:</span>
                    <span data-testid="text-detail-date">{formatDate(selectedOrder.createdAt)}</span>
                  </div>
                  <div className="flex justify-between items-center text-sm mt-1">
                    <span>Status:</span>
                    <Badge className={getStatusColor(selectedOrder.status)} data-testid="badge-detail-status">
                      {selectedOrder.status.charAt(0).toUpperCase() + selectedOrder.status.slice(1)}
                    </Badge>
                  </div>
                </div>
              </div>
            </>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/admin/products-man.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Plus, Edit, Trash2, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAppContext } from "@/context/app-context";
import type { Product, InsertProduct, Category } from "@shared/schema";

export default function ProductsManagement() {
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [productData, setProductData] = useState<Partial<InsertProduct>>({});
  const [selectedFiles, setSelectedFiles] = useState<FileList | null>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { products, categories } = useAppContext();

  const createProductMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const res = await fetch("/api/admin/products", {
        method: "POST",
        body: formData,
        credentials: "include",
      });
      if (!res.ok) throw new Error("Failed to create product");
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Product created successfully" });
      handleCloseDialog();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to create product", description: error.message, variant: "destructive" });
    },
  });

  const updateProductMutation = useMutation({
    mutationFn: async ({ id, formData }: { id: string; formData: FormData }) => {
      const res = await fetch(`/api/admin/products/${id}`, {
        method: "PUT",
        body: formData,
        credentials: "include",
      });
      if (!res.ok) throw new Error("Failed to update product");
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Product updated successfully" });
      handleCloseDialog();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to update product", description: error.message, variant: "destructive" });
    },
  });

  const deleteProductMutation = useMutation({
    mutationFn: async (id: string) => {
      const res = await apiRequest("DELETE", `/api/admin/products/${id}`);
      return res;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Product deleted successfully" });
    },
    onError: (error: Error) => {
      toast({ title: "Failed to delete product", description: error.message, variant: "destructive" });
    },
  });

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  const handleCreate = () => {
    setEditingProduct(null);
    setProductData({});
    setSelectedFiles(null);
    setIsCreateOpen(true);
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setProductData(product);
    setSelectedFiles(null);
    setIsCreateOpen(true);
  };

  const handleCloseDialog = () => {
    setIsCreateOpen(false);
    setEditingProduct(null);
    setProductData({});
    setSelectedFiles(null);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("productData", JSON.stringify(productData));

    if (selectedFiles) {
      Array.from(selectedFiles).forEach(file => {
        formData.append("images", file);
      });
    }

    if (editingProduct) {
      updateProductMutation.mutate({ id: editingProduct.id, formData });
    } else {
      createProductMutation.mutate(formData);
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this product?")) {
      deleteProductMutation.mutate(id);
    }
  };

  const isLoading = createProductMutation.isPending || updateProductMutation.isPending;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-serif font-bold mb-2">Product Management</h2>
          <p className="text-muted-foreground">Manage your pet shop inventory</p>
        </div>
        <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleCreate} data-testid="button-add-product">
              <Plus className="mr-2 h-4 w-4" />
              Add Product
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto forest-card">
            <DialogHeader>
              <DialogTitle>
                {editingProduct ? "Edit Product" : "Create New Product"}
              </DialogTitle>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="product-name">Name *</Label>
                  <Input
                    id="product-name"
                    type="text"
                    value={productData.name || ""}
                    onChange={(e) => setProductData(prev => ({ ...prev, name: e.target.value }))}
                    required
                    disabled={isLoading}
                    data-testid="input-product-name"
                  />
                </div>

                <div>
                  <Label htmlFor="product-category">Category *</Label>
                  <Select 
                    value={productData.categoryId || ""} 
                    onValueChange={(value) => setProductData(prev => ({ ...prev, categoryId: value }))}
                    disabled={isLoading}
                  >
                    <SelectTrigger data-testid="select-product-category">
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                    <SelectContent>
                      {categories?.map((category) => (
                        <SelectItem key={category.id} value={category.id}>
                          {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="product-type">Type *</Label>
                  <Select 
                    value={productData.type || ""} 
                    onValueChange={(value: "pet" | "food" | "accessory") => setProductData(prev => ({ ...prev, type: value }))}
                    disabled={isLoading}
                  >
                    <SelectTrigger data-testid="select-product-type">
                      <SelectValue placeholder="Select type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="pet">Pet</SelectItem>
                      <SelectItem value="food">Food</SelectItem>
                      <SelectItem value="accessory">Accessory</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="product-species">Species</Label>
                  <Input
                    id="product-species"
                    type="text"
                    placeholder="e.g., Dog, Cat, Fish"
                    value={productData.species || ""}
                    onChange={(e) => setProductData(prev => ({ ...prev, species: e.target.value }))}
                    disabled={isLoading}
                    data-testid="input-product-species"
                  />
                </div>

                <div>
                  <Label htmlFor="product-price">Price (INR) *</Label>
                  <Input
                    id="product-price"
                    type="number"
                    min="1"
                    step="1"
                    value={productData.priceInINR || ""}
                    onChange={(e) => setProductData(prev => ({ ...prev, priceInINR: parseInt(e.target.value) }))}
                    required
                    disabled={isLoading}
                    data-testid="input-product-price"
                  />
                </div>

                <div>
                  <Label htmlFor="product-stock">Stock *</Label>
                  <Input
                    id="product-stock"
                    type="number"
                    min="0"
                    value={productData.stock || ""}
                    onChange={(e) => setProductData(prev => ({ ...prev, stock: parseInt(e.target.value) }))}
                    required
                    disabled={isLoading}
                    data-testid="input-product-stock"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="product-description">Description *</Label>
                <Textarea
                  id="product-description"
                  value={productData.description || ""}
                  onChange={(e) => setProductData(prev => ({ ...prev, description: e.target.value }))}
                  required
                  disabled={isLoading}
                  className="h-20"
                  data-testid="input-product-description"
                />
              </div>

              <div>
                <Label htmlFor="product-images">Images</Label>
                <Input
                  id="product-images"
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={(e) => setSelectedFiles(e.target.files)}
                  disabled={isLoading}
                  data-testid="input-product-images"
                />
                <p className="text-sm text-muted-foreground mt-1">
                  Select up to 5 images. Leave empty to keep existing images when editing.
                </p>
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button type="button" variant="outline" onClick={handleCloseDialog} disabled={isLoading}>
                  Cancel
                </Button>
                <Button type="submit" disabled={isLoading} data-testid="button-save-product">
                  {isLoading ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      {editingProduct ? "Updating..." : "Creating..."}
                    </>
                  ) : (
                    editingProduct ? "Update Product" : "Create Product"
                  )}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Products Grid */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products?.map((product) => (
          <Card key={product.id} className="forest-card">
            <CardHeader className="pb-3">
              {product.images.length > 0 ? (
                <img 
                  src={product.images[0]} 
                  alt={product.name}
                  className="w-full h-32 object-cover rounded-lg"
                  data-testid={`img-admin-product-${product.id}`}
                />
              ) : (
                <div className="w-full h-32 bg-muted rounded-lg flex items-center justify-center text-4xl">
                  {product.type === "pet" ? "ðŸ¾" : product.type === "food" ? "ðŸ¥˜" : "ðŸŽ¾"}
                </div>
              )}
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                <div className="flex justify-between items-start">
                  <h3 className="font-semibold" data-testid={`text-admin-product-name-${product.id}`}>
                    {product.name}
                  </h3>
                  <Badge variant="secondary" data-testid={`badge-admin-product-type-${product.id}`}>
                    {product.type}
                  </Badge>
                </div>

                <p className="text-sm text-muted-foreground" data-testid={`text-admin-product-price-${product.id}`}>
                  {formatPrice(product.priceInINR)}
                </p>

                <p className="text-sm text-muted-foreground" data-testid={`text-admin-product-stock-${product.id}`}>
                  Stock: {product.stock}
                </p>

                <div className="flex space-x-2 pt-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleEdit(product)}
                    className="flex-1"
                    data-testid={`button-edit-product-${product.id}`}
                  >
                    <Edit className="h-4 w-4 mr-1" />
                    Edit
                  </Button>
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => handleDelete(product.id)}
                    className="flex-1"
                    disabled={deleteProductMutation.isPending}
                    data-testid={`button-delete-product-${product.id}`}
                  >
                    <Trash2 className="h-4 w-4 mr-1" />
                    Delete
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

        {products?.length === 0 && (
          <div className="col-span-full text-center py-12" data-testid="text-no-products-admin">
            <p className="text-muted-foreground text-lg">No products found. Add your first product to get started.</p>
          </div>
        )}
      </div>
    </div>
  );
}

